mapper.select:
SELECT topic_id
FROM user_stats
WHERE user_id = #{userId}
  AND resource_type = #{resourceType}
  AND resource_id = #{resourceId}
  AND topic_stats = 'done';

SELECT topic_id
FROM user_stats
WHERE user_id = #{userId}
  AND resource_type = #{resourceType}
  AND resource_id = #{resourceId}
  AND topic_stats = 'learning';

SELECT topic_id
FROM user_stats
WHERE user_id = #{userId}
  AND resource_type = #{resourceType}
  AND resource_id = #{resourceId}
  AND topic_stats = 'skipped';
